// åˆ¤æ–·ä¾†æº
function detectSource(link) {
  const url = link.toLowerCase();

  // å°ç£ä¾†æº
  if (url.includes('technews.tw')) return 'technews';
  if (url.includes('ithome.com.tw')) return 'ithome';
  if (url.includes('inside.com.tw')) return 'inside';

  // åœ‹éš›ä¾†æº
  if (url.includes('hackernews') || url.includes('feedburner')) return 'hackernews';
  if (url.includes('techcrunch')) return 'techcrunch';
  if (url.includes('arstechnica')) return 'arstechnica';
  if (url.includes('openai')) return 'openai';
  if (url.includes('berkeley') || url.includes('bair')) return 'bair';

  return 'unknown';
}

// è©•åˆ†å‡½æ•¸ï¼ˆåŠ å…¥å°ç£è¦–è§’ï¼‰
function calculateRelevance(item) {
  const title = (item.json.title || '').toLowerCase();
  const content = (item.json.content || '').toLowerCase();
  const link = item.json.link || '';
  const fullText = `${title} ${content}`;

  const source = detectSource(link);
  const config = FILTERS.sources[source] || {
    priority_keywords: [],
    exclude: [],
    base_score: 0
  };

  let score = config.base_score || 0;

  // 1. å¿…é ˆä¿ç•™
  for (const phrase of FILTERS.must_keep_phrases) {
    if (fullText.includes(phrase.toLowerCase())) {
      return 100;
    }
  }

  // 2. æ’é™¤é—œéµå­—
  for (const keyword of config.exclude) {
    if (fullText.includes(keyword.toLowerCase())) {
      score -= 5;
    }
  }

  // 3. ä¾†æºå„ªå…ˆé—œéµå­—
  for (const keyword of config.priority_keywords) {
    const keywordLower = keyword.toLowerCase();
    if (title.includes(keywordLower)) {
      score += 10;
    } else if (content.includes(keywordLower)) {
      score += 5;
    }
  }

  // 4. å°ç£èˆˆè¶£é—œéµå­—ï¼ˆé¡å¤–åŠ åˆ†ï¼‰
  for (const keyword of FILTERS.taiwan_interests) {
    if (fullText.includes(keyword.toLowerCase())) {
      score += 4;
    }
  }

  // 5. å…¨çƒä½†å°ç£é—œæ³¨çš„ä¸»é¡Œ
  for (const keyword of FILTERS.global_taiwan_focus) {
    if (fullText.includes(keyword.toLowerCase())) {
      score += 6;
    }
  }

  // 6. ç‰¹æ®Šè™•ç†
  const isTaiwanSource = ['technews', 'ithome', 'inside'].includes(source);
  const isInternationalSource = ['hackernews', 'techcrunch', 'openai'].includes(source);

  if (isTaiwanSource) {
    // æœ¬åœ°æ–°èé¡å¤–åŠ åˆ†
    score += 5;

    // å¦‚æœåœ‹éš›æ–°èæåˆ°å°ç£ï¼Œå¤§å¹…åŠ åˆ†
    if (fullText.includes('åœ‹éš›') || fullText.includes('global')) {
      score += 8;
    }
  }

  if (isInternationalSource) {
    // åœ‹éš›æ–°èå¦‚æœæåˆ°å°ç£æˆ–äºæ´²ï¼ŒåŠ åˆ†
    if (fullText.includes('taiwan') || fullText.includes('asia')) {
      score += 10;
    }
  }

  // 7. å¯¦ç”¨æ€§åŠ åˆ†
  const practicalKeywords = ['æ•™å­¸', 'tutorial', 'guide', 'å¯¦æ¸¬', 'è©•æ¸¬', 'æ¯”è¼ƒ'];
  for (const keyword of practicalKeywords) {
    if (title.includes(keyword)) {
      score += 7;
    }
  }

  // 8. å…§å®¹é•·åº¦
  if (content.length > 300) score += 2;
  if (content.length > 500) score += 2;

  return score;
}

