  return labels[source] || 'ğŸ“° å…¶ä»–';
}

// æ’åºå’Œé™åˆ¶
function sortAndLimit(arr, source) {
  const config = FILTERS.sources[source];
  const max = config ? config.max_items : 5;

  return arr
    .sort((a, b) => b.json.relevance_score - a.json.relevance_score)
    .filter(i => i.json.relevance_score > 0)
    .slice(0, max);
}

