  // åˆ†æ•¸æ¥è¿‘æ™‚ï¼Œäº¤æ›¿æœ¬åœ°èˆ‡åœ‹éš›
  const aIsTaiwan = ['technews', 'ithome', 'inside'].includes(a.json.source);
